CREATE DATABASE mydatabase;

CREATE TABLE PLACE (
	STT	SERIAL primary KEY,
	DiaChi	TEXT,
	KhuVuc	TEXT,
	LoaiVT	TEXT,
	HinhThuc TEXT,
	HinhAnh	TEXT,
	QuyHoach TEXT,
	Latitude FLOAT8,
	Longitude FLOAT8
);

CREATE TABLE PLACE_DETAILS (
  STT	SERIAL primary KEY,
  PLACE_STT SERIAL,
  AD_NAME TEXT,
  AD_SIZE TEXT,
  AD_QUANTITY SERIAL,
  EXPIRE_DATE DATE,
  IMG_PATH TEXT,
  CONSTRAINT fk_place_stt FOREIGN KEY(PLACE_STT) REFERENCES PLACE(STT)
);


